mapper.select:
SELECT * FROM Likeit
WHERE
	gg_no=#{gg_no} and m_no=#{m_no}

SELECT * FROM group_enroll
WHERE
	gg_no=#{gg_no} and m_no=#{m_no}

        SELECT COUNT(*) AS CNT
		FROM COMMENTS
		WHERE c_parent=#{c_no}

        SELECT COUNT(*) AS PAR
		FROM COMMENTS
		WHERE c_group=#{c_group}

mapper.update:
UPDATE COMMENTS
SET C_DEL = 'Y'
WHERE c_no=#{c_no}
