mapper.delete:

DELETE
FROM PLACEBOOK
WHERE L_NO = #{L_NO}
AND U_DATE = TO_DATE(#{U_DATE},'YYYY-MM-DD') 
AND M_NO = #{M_NO}

mapper.update:

	UPDATE REVIEW
   		SET R_CONTENT=#{R_CONTENT} 
 	WHERE R_NO=#{R_NO}

mapper.select:
        SELECT COUNT(*) AS CNT
		FROM REVIEW
		WHERE R_PARENT=#{R_NO}

        SELECT COUNT(*) AS PAR
		FROM REVIEW
		WHERE R_GROUP=#{R_GROUP}

mapper.update:
UPDATE REVIEW
SET R_DELETEFLAG = 'Y'
WHERE R_NO=#{R_NO}
